        <script>
            var up = FileUpload('#fileupload');
            up.init({
                url: '../upload/index.php',
                done : function done(e, data) {
                    var file = data.result.name;
                    var $wrap = $('<span class="fItem" />');
                    var $exibir = $('<a/>')
                            .attr('target', '_blank')
                            .prop('href', '../userfiles/news/files/'+file)
                            .text('exibir');		
                    
                    var $del = $('<a/>')
                            .attr('target', '_blank')
                            .prop('href', '../upload/deletar.php?img=../userfiles/news/files/'+file)
                            .text('del');	
                    
                    $del.click(function(){
                        $(this).parent()
                                .remove();
                    });
                    
                    $wrap.append($exibir);
                    $wrap.append('<span> - </span>');
                    $wrap.append($del);
                    
                    $('#fileupload').siblings('span').text('Add Files');
                    $('#files').append($wrap);
                    
                    var postData = 'classe=\\App\\Models\\Newsletter&method=moveFile&file='+file;
                   common.ajax(postData, function(){});
                }
            });
        </script>
		
